                                   if @Qty1GrossTotal is null
                                      begin
                                         select @Qty1GrossTotal = 0
                                      end
							  
							  end
					   end
					else if (@ItemType = 2)
					   begin
					   
					     -- select supplier and name   of particular cost center item
