
CREATE PROCEDURE [dbo].[sp_users_get_USER_PEREFERENCES_BY_USER_ID]
(@SystemUserID int)
AS
	SELECT tbl_systemuser_preferences.SystemUserPreferenceID,tbl_systemuser_preferences.SystemUserID,tbl_systemuser_preferences.CallColor,tbl_systemuser_preferences.AppointmentColor,tbl_systemuser_preferences.NextActionColor,tbl_systemuser_preferences.OtherActionColor,tbl_systemuser_preferences.SaleColor,tbl_systemuser_preferences.EventColor, tbl_systemuser_preferences.ToDoColor,tbl_systemuser_preferences.RecordRetrievalLimit,tbl_systemuser_preferences.EnquiryRefreshTime, tbl_systemuser_preferences.EstimatesRefreshTime, tbl_systemuser_preferences.OrdersRefreshTime, tbl_systemuser_preferences.JobsRefreshTime, tbl_systemuser_preferences.SchedulingRefreshTime,tbl_systemuser_preferences.ShowPublic,tbl_systemuser_preferences.CRMReminderRefreshTime,SmtpServer,SmtpUserName,SmtpPassword,Pop3Server,Pop3UserName,Pop3Password,DefaultCalendar  FROM tbl_systemuser_preferences WHERE tbl_systemuser_preferences.SystemUserID = @SystemUserID;
	RETURN